import React, { useState } from "react";

export default function App() {
  const [messages, setMessages] = useState([
    { sender: "bot", text: "Hi, Iâ€™m NuruMindfulness ðŸ‘‹. How are you feeling today?" },
  ]);
  const [input, setInput] = useState("");

  // very simple reply logic for MVP
  const botReply = (userMessage) => {
    const lower = userMessage.toLowerCase();

    if (lower.includes("sad") || lower.includes("down")) {
      return "Iâ€™m sorry youâ€™re feeling this way ðŸ’™. Remember itâ€™s okay to not be okay. Do you want to try a short breathing exercise together?";
    }
    if (lower.includes("stressed") || lower.includes("anxious")) {
      return "Stress can feel overwhelming. Try this: breathe in slowly for 4 seconds, hold for 4, then exhale for 4. Want me to guide you through it?";
    }
    if (lower.includes("happy") || lower.includes("good")) {
      return "Thatâ€™s wonderful to hear ðŸ˜ƒ. Whatâ€™s one thing that made you feel good today?";
    }
    if (lower.includes("lonely")) {
      return "Feeling lonely can be really tough. Youâ€™re not alone here. Would you like me to share some ways to stay connected?";
    }

    // default fallback
    return "Thank you for sharing. Can you tell me a bit more about how youâ€™re feeling?";
  };

  const sendMessage = () => {
    if (!input.trim()) return;

    const userMessage = { sender: "user", text: input.trim() };
    const reply = { sender: "bot", text: botReply(input.trim()) };

    setMessages([...messages, userMessage, reply]);
    setInput("");
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") sendMessage();
  };

  return (
    <div className="h-screen flex flex-col bg-gradient-to-b from-blue-50 to-blue-100">
      {/* Header */}
      <header className="bg-blue-600 text-white text-center py-4 shadow-md">
        <h1 className="text-xl font-bold">NuruMindfulness</h1>
        <p className="text-sm">A safe space for menâ€™s mental well-being</p>
      </header>

      {/* Chat window */}
      <div className="flex-1 overflow-y-auto p-4 space-y-3">
        {messages.map((msg, i) => (
          <div
            key={i}
            className={`flex ${msg.sender === "user" ? "justify-end" : "justify-start"}`}
          >
            <div
              className={`px-4 py-2 rounded-2xl shadow-md max-w-xs ${
                msg.sender === "user"
                  ? "bg-blue-600 text-white rounded-br-none"
                  : "bg-white text-gray-800 rounded-bl-none"
              }`}
            >
              {msg.text}
            </div>
          </div>
        ))}
      </div>

      {/* Input */}
      <div className="p-3 bg-white flex items-center shadow-inner">
        <input
          type="text"
          className="flex-1 border border-gray-300 rounded-xl px-4 py-2 mr-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
          placeholder="Type your message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyPress}
        />
        <button
          onClick={sendMessage}
          className="bg-blue-600 text-white px-4 py-2 rounded-xl shadow hover:bg-blue-700 transition"
        >
          Send
        </button>
      </div>
    </div>
  );
}
